<?php  require_once "seguridad/sql_inject.php"; $bDestroy_session = TRUE; $url_redirect = 'index.php'; $sqlinject = new sql_inject('./log_file_sql.log',$bDestroy_session,$url_redirect); session_start(); error_reporting(0); include_once('./kev/pdo.php'); if(isset($_SESSION['User']) && isset($_GET['f'])) { $faccj = $_GET['f']; $user_ses = $_SESSION['User']; $dbm = $con->prepare("SELECT * FROM `invitaciones` WHERE `Invitado` = :usuario"); $dbm->bindParam(':usuario', $user_ses, PDO::PARAM_STR); $dbm->execute(); $num_rows = $dbm->rowCount(); if($num_rows > 0) { while($row = $dbm->fetch()) { if($row['BandaID'] == $faccj) { $stmt = $con->prepare("DELETE FROM `invitaciones` WHERE `Invitado` = :usuario AND `BandaID` = :faccion"); $stmt->bindParam(':usuario', $user_ses, PDO::PARAM_STR); $stmt->bindParam(':faccion', $faccj, PDO::PARAM_INT); $stmt->execute(); echo '5'; return 0; } else { echo '2'; return 0; } } } else { echo '1'; return 0; } } else { echo "<script>window.location='index.php';</script>"; } ?>